import React from "react";
import axios from "axios";

var i = 1;

class Test extends React.Component {

  componentDidMount(){

    axios
        .get("https://pokeapi.co/api/v2/pokemon")
        .then(response =>
        response.data.results.map((user, index) => ({
            name: `${user.name}`,
            count: `${user.count}`,
            index: i++
        }))
        )
        .then(users => {
        this.setState({
            users,
            isLoading: false
        });
        })
        .catch(error => this.setState({ error, isLoading: false }));

  }

}
export default Test;
