import React from "react";
import { loadOptions } from "@babel/core";
import Loader from "./Loader";
import axios from "axios";

class List extends React.Component {
  state = {
    isLoading: true,
    users: [],
    error: null
  };


  componentDidMount() {

    var url = "https://pokeapi.co/api/v2/pokemon"

    var name = url.split('/')[5]
    console.log(name)

    const response = fetch("https://pokeapi.co/api/v2/pokemon")





    fetch("https://pokeapi.co/api/v2/pokemon")
    .then(response => {
        return response.json()
      })
      .then(data => {
        // Work with JSON data here
        console.log(data.results)
      })
      .catch(error => this.setState({ error, isLoading: false }));

    //   axios
    //   .get("https://pokeapi.co/api/v2/pokemon/:id")
    //   .then(response => 
    //     response.data.results.map((url, index) => ({
    //        index: i++,
    //        url: `https://pokeapi.co/api/v2/pokemon/${index}`,
    //     })))






  }

  render() {
    const { isLoading, users } = this.state;
    return (
      <React.Fragment>
        <h2>Pokemon</h2>
        <div>
          {
            users.map(user => {
              const { name, index, url } = user;
              return (
                <div key={name}>
                  <div>
                    <img alt={name} />
                    {isLoading ? <Loader /> : <p> {index} </p> } 
                    {isLoading ? <Loader /> : <p> {url} </p> } 
                  </div>

                  <hr />
                </div>
              );
            })

          }
        </div>
      </React.Fragment>
    );
  }
}

export default List;
