import React from "react";
import axios from "axios";

class PokeFind extends React.Component {
  state = {
    isLoading: false,
    name: '',
    weight: '',
    height: '',
    error: null
  };

  componentDidMount() {

    var url = "https://pokeapi.co/api/v2/pokemon?offset=50&limit=50";

    const id = this.props.match.params.id;

    axios
    .get(`https://pokeapi.co/api/v2/pokemon/${id}`)
    .then(response =>
      this.state.weight = response.data.weight,
      console.log(response.data.height)
    )
    // .then(response =>
    //     this.state.height = ,

    //     )




    .then(users => {
        this.setState({
          users,
          isLoading: false
        });
      })
      .catch(error => this.setState({ error, isLoading: false }));
  }

  render() {

    const { isLoading, users, weight, height } = this.state;
    return (
      <React.Fragment>
        <h2>Pokemon</h2>
        <div>



              <div className="row" >
                <p>{weight}</p>
                <p>{height}</p>
              </div>


        </div>
      </React.Fragment>
    );
  }
}


export default PokeFind;
