    // }

    //méthode ES6

    async componentDidMount() {

        this.setState({ isLoading: true});

        try {


            const res = await fetch('https://pokeapi.co/api/v2/pokemon');
            const data = await res.json();


            this.setState({ name: data.results.map(({ name }) => name), isLoading: false });



        }
        catch(err)
        {
            console.log(err);
            throw err;
        }
    }

    render() {

        const { isLoading, name} = this.state;

        return (
            <div>
                <h1>Lorem</h1>
                {isLoading ? <Loader /> : <p> {name} </p> } 
            </div>

        )
}

}



export default List;
