    //méthode ES6


    async componentDidMount() {
        axios
          .fetch("https://pokeapi.co/api/v2/pokemon")
          .then(response =>
            response.data.results.map((user, index) => ({
              name: `${user.name}`,
              count: `${user.count}`,

            }))

          )

          .then(users => {
            this.setState({
              users,
              isLoading: false
            });
          })
          .catch(error => this.setState({ error, isLoading: false }));



      }




      render() {
        const { isLoading, users } = this.state;
        return (
          <React.Fragment>
            <h2>Pokemon</h2>
            <div>
              {!isLoading ? (
                users.map(user => {
                  const {  name } = user;
                  return (
                    <div key={name}>
                      <div>
                        <img alt={name} />
                        <p>index</p>
                      </div>

                      <hr />
                    </div>
                  );
                })
              ) : (
                <p>Loading...</p>
              )}
            </div>
          </React.Fragment>
        );
      }

    }





export default List;
