import React from "react";
import { loadOptions } from "@babel/core";
import Loader from "./Loader";
import axios from "axios";
import Test from "./NamePokemon"

class List extends React.Component {
  state = {
    isLoading: true,
    users: [],
    error: null
  };

  // méthode ES5

  // componentDidMount() {
  //     fetch('https://baconipsum.com/api/?type=meat-and-filler')
  //     .then( res => res.json())
  //     .then( data => this.setState({ data }))
  //     .catch(err => {
  //         console.log(err);
  //         throw err;
  //         });
  // }

  //méthode ES6

  componentDidMount() {




  }

  render() {
    const { isLoading, users } = this.state;
    return (
      <React.Fragment>
          <Test />
        <h2>Pokemon</h2>
        <div>
          {!isLoading ? (
            users.map(user => {
              const { name, index } = user;
              return (
                <div key={name}>
                  <div>
                    <img alt={name} />
                    <p>{index}</p>
                  </div>

                  <hr />
                </div>
              );
            })
          ) : (
            <p>Loading...</p>
          )}
        </div>
      </React.Fragment>
    );
  }
}

export default List;
