import React from "react";
import axios from "axios";

class PokeFind extends React.Component {
  state = {
    isLoading: false,
    users: [],
    error: null
  };

  componentDidMount() {

    var url = "https://pokeapi.co/api/v2/pokemon?offset=50&limit=50";

    const id = this.props.match.params.id;

    axios
    .get(`/pokemon/${id}`)
    .then(response =>
      response.data.results.map(
        (user, n, url, p) => (
           n = id,
           user = `${user.url}`,
           url = user.split("/")[6] ,
           p = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${url}.png`,
          {
            name: n,
            username: user,
            pics: p,
            id
          }
        )
      )
    )
    .then(users => {
        this.setState({
          users,
          isLoading: false
        });
      })
      .catch(error => this.setState({ error, isLoading: false }));
  }

  render() {

    const { isLoading, users } = this.state;
    return (
      <React.Fragment>
        <h2>Pokemon</h2>
        <div>
          {users.map(user => {
            const { name, pics } = user;

            return (
              <div className="row" >
                <p>{name}</p>

              </div>
            );
          })}
        </div>
      </React.Fragment>
    );
  }
}


export default PokeFind;
