import React from "react";
import { loadOptions } from "@babel/core";
import Loader from "./Loader";
import axios from "axios";
import "./List.css";

class List extends React.Component {
  state = {
    isLoading: true,
    users: [],
    error: null
  };

  componentDidMount() {
    var url = "https://pokeapi.co/api/v2/pokemon?offset=50&limit=50";

    axios
      .get(url)
      .then(response =>
        response.data.results.map(
          (user, n, url, p) => (
            (n = `${user.name}`),
            (user = `${user.url}`),
            (url = user.split("/")[6]),
            (p = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/back/${url}.png`),
            {
              name: n,
              username: user,
              pics: p
            }
          )
        )
      )

      .then(users => {
        this.setState({
          users,
          isLoading: false
        });
      })
      .catch(error => this.setState({ error, isLoading: false }));
  }

  render() {
    const { isLoading, users } = this.state;
    return (
      <React.Fragment>
        <h2>Pokemon</h2>
        <div>
          {users.map(user => {
            const { name, index, pics } = user;

            return (





              <ul class="card-list" key={name}>
                <li class="card">
                  <a
                    class="card-image"
                    href="https://michellezauner.bandcamp.com/album/psychopomp-2"
                    target="_blank"
                    style="background-image: url({pics});"
                    data-image-full={pics}
                  >
                    <img src={pics} alt="Psychopomp" />
                  </a>
                  <a
                    class="card-description"
                    href="https://michellezauner.bandcamp.com/album/psychopomp-2"
                    target="_blank"
                  >
                    <h2>{name}</h2>
                  </a>
                </li>
              </ul>

              // <div key={name}>
              //   <div>
              //   <a href={pics}><img alt="" src={pics} /></a>
              //     {isLoading ? <Loader /> : <p> {index} </p> }
              //     {isLoading ? <Loader /> : <p> {name} </p> }
              //   </div>

              //   <hr />
              // </div>
            );
          })}
        </div>
      </React.Fragment>
    );
  }
}

export default List;
