            const data = await res.json();

            let ArrayName = []
            for (var i = 0; i < data.results.length; i++) {
                ArrayName.push(data.results[i].name)

            }




            let ArrayPics = []
            for (var i = 0; i < data.results.length; i++) {
                ArrayPics.push(data.results[i].name.sprites.back_default)

            }

            console.log(ArrayPics);

        this.setState({ data: ArrayName, pics: ArrayPics, isLoading: false });

        }
        catch(err)
        {
            console.log(err);
            throw err;
        }
    }

    render() {

        const { isLoading, data} = this.state;

        return (
            <div>
                <h1>Lorem</h1>
                {isLoading ? <Loader /> : <p> {data} </p> } 
            </div>

        )
}

}



export default List;
