import React from "react";
import { loadOptions } from "@babel/core";
import Loader from "./Loader";
import axios from "axios";

class List extends React.Component {
  state = {
    isLoading: false,
    users: [],
    error: null
  };


  componentDidMount() {

    axios
      .get("https://pokeapi.co/api/v2/pokemon")
      .then(response =>
        response.data.results.map((user, index = 1) => ({
          name: `${user.name}`,
          count: `${user.count}`,
          url: `https://pokeapi.co/api/v2/pokemon/${index}`,
        }))
      )

      .then(users => {
        this.setState({
          users,
          isLoading: true,
        });
      })
      .catch(error => this.setState({ error, isLoading: false }));

    //   axios
    //   .get("https://pokeapi.co/api/v2/pokemon/:id")
    //   .then(response => 
    //     response.data.results.map((url, index) => ({
    //        index: i++,
    //        url: `https://pokeapi.co/api/v2/pokemon/${index}`,
    //     })))

  }

  render() {
    const { isLoading, users } = this.state;
    return (
      <React.Fragment>
        <h2>Pokemon</h2>
        <div>
          {!isLoading ? (
            users.map(user => {
              const { name, index, url } = user;
              return (
                <div key={name}>
                  <div>
                    <img alt={name} />
                    <p>{index}</p>
                    <p>{url}</p>
                  </div>

                  <hr />
                </div>
              );
            })
          ) : (
            <p>Loading...</p>
          )}
        </div>
      </React.Fragment>
    );
  }
}

export default List;
